// Scope       : Pre Loader
// Execution   : n/a
// Condition   : n/a
// Description : We need to keep multiple utag_main cookies, because TiQ is designed to only have 1 profile 
//               implemented per domain, and 1und1 / 1und1hosting share TLDs (1und1.de, more to follow).
//
//               This extension makes sure that the cookies are correctly in place before TiQ loads.

// HOSTING

(function() {
  var prefix = &quot;utmain_&quot;;
  var profileCookieName = prefix + &quot;hosting&quot;;
  var firstCookieName = prefix + &quot;first&quot;;
  var mainCookieName = &quot;utag_main&quot;;

  // make sure the global functions are there
  if (!window.teal || !window.teal.util) {
    //console.log(&quot;No function definitions found&quot;);
    return false;
  }
  var util = window.teal.util;

  var profileCookieValue = util.readCookie(profileCookieName);
  var mainCookieValue = util.readCookie(mainCookieName);

  var currentDomain = util.getCurrentDomain();
  var onGermanSite = (currentDomain === &quot;1und1.de&quot;);

  // just let TiQ run if no cookies present
  if (!mainCookieValue &amp;&amp; !profileCookieValue) return; 

  // germany
  if (onGermanSite) {
    if (mainCookieValue) {
      // if a main cookie is found, store it for Access to pick up later...
      util.writeCookie(firstCookieName, mainCookieValue);
      // ...then erase it to avoid re-using it for this profile.
      util.eraseCookie(mainCookieName);
    }
    // write the main cookie with the profile one if found
    if (profileCookieValue) {
      util.writeCookie(mainCookieName, profileCookieValue);
      return;
    }
  }
  // other countries
  else {
    // use the profile cookie if present
    if (profileCookieValue) {
      util.writeCookie(mainCookieName, profileCookieValue);
    } else {
      // erase any found main cookie if the another prefixed cookie is found (otherwise use it)
      if (util.checkForCookiePrefix(prefix)) util.eraseCookie(mainCookieName);
    }
  }
}());

