// Description : We need to keep multiple utag_main cookies, because TiQ is designed to only have 1 profile 
//               implemented per domain, and 1und1 / 1und1hosting share TLDs (1und1.de, more to follow).
//
//               This extension defines a utility object of functions for use by the others in the group.


var teal = window.teal || {};
var util = teal.util = teal.util || {};

// lifted from utag.loader.lh
util.getCurrentDomain = function(a, b, c) {
  a = &quot;&quot; + location.hostname;
  b = a.split(&quot;.&quot;);
  c = (/\.co\.|\.com\.|\.org\.|\.edu\.|\.net\.|\.asn\.|\...\.jp$/.test(a)) ? 3 : 2;
  return b.splice(b.length - c, c).join(&quot;.&quot;);
}

util.getCookieDomain = function() {
  return util.getCurrentDomain();
}

util.writeCookie = function(name, value, days) {
  var expires = &quot;&quot;;
  days = days || 365;
  if (days) {
    var date = new Date();
    date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
    expires = &quot;; expires=&quot;+date.toGMTString();
  }
  document.cookie = name + &quot;=&quot; + value + expires + &quot;;domain=&quot; + util.getCookieDomain() + &quot;;path=/&quot;;
  return true;
}

util.readCookie = function(name) {
  var reString = '(?:(?:^|.*;\\s*)' + name + '\\s*\\=\\s*([^;]*).*$)|^.*$';
  var re = new RegExp(reString);
  var cookieValue = document.cookie.replace(re, &quot;$1&quot;);
  if (!cookieValue) return undefined;
  return cookieValue;
}

util.eraseCookie = function(name) {
  document.cookie = name + '=deleted;expires=Thu, 01 Jan 1970 00:00:01 GMT;domain=' + util.getCookieDomain() + ';path=/;';
}

util.checkForCookiePrefix = function(prefix) {
  var cookies = document.cookie.split(&quot;;&quot;);
  var reString = '^\\s*' + prefix + '.*';
  var re = new RegExp(reString);
  for (var i = 0, name, match; i &lt; cookies.length; i++){
    name = cookies[i].split(&quot;=&quot;)[0];
    match = re.test(name);
    if (match) return true;
  }
  return false;
};

