/***************************************************************************
	MODIFYING THE CODE BELOW THIS WILL AFFECT THE CONSENT MANAGEMENT.
	  PLEASE CONTACT SUPPORT FOR ASSISTANCE IF YOU NEED TO MODIFY.
***************************************************************************/
(function preferences_prompt() {
    
    /*utag.link({
        &quot;tealium_event&quot; : &quot;consent manager&quot;,
        &quot;consent_view&quot; : &quot;preferences&quot;,
        &quot;action&quot; : &quot;view&quot;,
        &quot;label&quot; : &quot;layer page 2&quot;
        }, null, [137]
    );*/
    
    
    //start AM - roman.martchinkovski@aktion-mensch.de
  	var $js_device;
		if (utag.data['dom.viewport_width'] &lt;500){
			$js_device = &quot;mobile&quot;;
		} else {
			$js_device = &quot;desktop&quot;;
		}
