    if  (!(utag.data['dom.domain'] &amp;&amp; utag.data['dom.domain'].indexOf(&quot;.test-am.de&quot;) &gt; -1)){
    
    var event = {
        &quot;tealium_account&quot;: &quot;aktionmensch&quot;,
        &quot;tealium_profile&quot;: &quot;consent-logging&quot;,
        &quot;tealium_event&quot;: &quot;consent manager&quot;,
        &quot;tealium_visitor_id&quot;: utag.data['cp.utag_main_v_id'],
        &quot;consent_view&quot;: &quot;preferences&quot;,
        &quot;action&quot;: &quot;view&quot;,
        &quot;label&quot;: &quot;layer page 2&quot;,
		&quot;js_device&quot;: $js_device
    };
    var xhr = new XMLHttpRequest();
    xhr.open(&quot;POST&quot;, &quot;https://collect.tealiumiq.com/event&quot;);
    xhr.send(JSON.stringify(event));
    };
    
  var $el = document.getElementById(&quot;accept_all&quot;),
      $save = document.getElementById(&quot;save&quot;),
      $modal = document.getElementById(&quot;GDPR_Modal_pref&quot;),
      $toggle = document.getElementById(&quot;preference&quot;),
      i;
  
  var acceptAll = function () {
    closePrompt();
    //start AM - roman.martchinkovski@aktion-mensch.de
        //set 2 year consent cookie
        utag.cfg.consentPeriod = 365;
    //end AM - roman.martchinkovski@aktion-mensch.de
	utag.gdpr.setConsentValue(true);
    /*utag.link({
        &quot;tealium_event&quot; : &quot;consent manager&quot;,
        &quot;consent_view&quot; : &quot;preferences&quot;,
        &quot;action&quot; : &quot;click&quot;,
        &quot;label&quot; : &quot;Alle Cookies akzeptieren&quot;
        }, null, [137]
    );*/
    if  (!(utag.data['dom.domain'] &amp;&amp; utag.data['dom.domain'].indexOf(&quot;.test-am.de&quot;) &gt; -1)){
    var event = {
        &quot;tealium_account&quot;: &quot;aktionmensch&quot;,
        &quot;tealium_profile&quot;: &quot;consent-logging&quot;,
        &quot;tealium_event&quot;: &quot;consent manager&quot;,
        &quot;tealium_visitor_id&quot;: utag.data['cp.utag_main_v_id'],
        &quot;consent_view&quot;: &quot;preferences&quot;,
        &quot;action&quot;: &quot;click&quot;,
        &quot;label&quot;: &quot;Alle Cookies akzeptieren&quot;,
		&quot;js_device&quot;: $js_device
    };
    var xhr = new XMLHttpRequest();
    xhr.open(&quot;POST&quot;, &quot;https://collect.tealiumiq.com/event&quot;);
    xhr.send(JSON.stringify(event));
    };
  };
  var saveConsent = function () {
      if  (!(utag.data['dom.domain'] &amp;&amp; utag.data['dom.domain'].indexOf(&quot;.test-am.de&quot;) &gt; -1)){
        var event = {
            &quot;tealium_account&quot;: &quot;aktionmensch&quot;,
            &quot;tealium_profile&quot;: &quot;consent-logging&quot;,
            &quot;tealium_event&quot;: &quot;consent manager&quot;,
            &quot;tealium_visitor_id&quot;: utag.data['cp.utag_main_v_id'],
            &quot;consent_view&quot;: &quot;preferences&quot;,
            &quot;action&quot;: &quot;click&quot;,
            &quot;label&quot;: &quot;Speichern - &quot; + $toggle.checked,
			&quot;js_device&quot;: $js_device
        };
        var xhr = new XMLHttpRequest();
        xhr.open(&quot;POST&quot;, &quot;https://collect.tealiumiq.com/event&quot;);
        xhr.send(JSON.stringify(event));
      };
    closePrompt();
    //start AM - roman.martchinkovski@aktion-mensch.de
        /*
        if (!$toggle.checked){
            utag.cfg.consentPeriod = 1;
        }
        utag.gdpr.setConsentValue($toggle.checked);
        */
        //set session cookie if $toggle.checked = false
        if (!$toggle.checked){
            var domain = utag.data['dom.domain'];
