    		document.cookie = &quot;CONSENTMGR=consent:false%7Cts:&quot;+utag.data['tealium_session_id']+ &quot;; domain=&quot;+ shortdomain+ &quot;; path=/;&quot;;
        }    
    
    	//set 2 year cookie if $toggle.checked = true
    	if ($toggle.checked){
    		utag.cfg.consentPeriod = 365;
    		utag.gdpr.setConsentValue($toggle.checked); 
    	}
      };
  
  //end AM - roman.martchinkovski@aktion-mensch.de
  var closePrompt = function () {
    $modal.style.display = &quot;none&quot;;
  };
  
  
  var consentState = utag.gdpr.getConsentState();
  if (typeof consentState === &quot;number&quot;) {
    var _state = false;
    if (consentState === 1 || consentState === -1){
      _state = consentState === 1;
    } else {
      _state = !!utag.gdpr.preferences_prompt.defaultState;
    }
    $toggle.checked = _state;
  }
  
  
  if (document.addEventListener) {
    $el.addEventListener(&quot;click&quot;, acceptAll, false);
    $save.addEventListener(&quot;click&quot;, saveConsent, false);
  } else if (document.attachEvent) {
    $el.attachEvent(&quot;click&quot;, acceptAll);
    $save.attachEvent(&quot;click&quot;, saveConsent);
  } else {
    $el.onclick = acceptAll;
