function createModelBucket(){

    var totalRevenue = Number(0);

    var _LS = 13; // Low Spender Limit from 0 --&gt; X
    var _HS = 20; // High Spender Limit --&gt; X

    for (var i=0; i &lt;= products.length - 1; i++) {
        //Look for a specific productID for ROAS Modelling
        if(products[i] == &quot;MGL&quot;){
            var qc_price = Number(utag_data.product_prices[i]);
            var qc_quantity = Number(utag_data.product_quantity[i]);

            var qc_result;

                qc_result = qc_price * qc_quantity;


                totalRevenue += qc_result;
        }
    };

    var _qc_total_amount  = totalRevenue; // Assign the NET value of the purchase to this variable;
    var _qc_bucket = &quot;&quot;; //Leave this variable as it is;

    //If totalRevenue is not a Number send back value 0;
    if (!isNaN(totalRevenue)) {

        if(_qc_total_amount){

            if (_qc_total_amount &gt;= 0 &amp;&amp; _qc_total_amount &lt;= _LS) {

                _qc_bucket = &quot;Low Spender&quot;;

            }else if(_qc_total_amount &gt;= _LS &amp;&amp; _qc_total_amount &lt;= _HS){

                _qc_bucket = &quot;Mid Spender&quot;;

            }else if (_qc_total_amount &gt;= _HS) {

                _qc_bucket = &quot;High Spender&quot;;

            }

        }else{

            _qc_bucket = &quot;N/A&quot;;

        }
    }

    return _qc_bucket;

}

