/**
 * Scope       : Tealium Collect
 * Execution   : n/a
 * Condition   : n/a
 * Description : Add some nice-to-have variables to the Collect payload for the GUA Connectors.
 *               These aren't required by Google,  but we want to match the client-side behavior as closely 
 *               as we can.
 * 
 *               Also add a copy of dom.* attributes as a workaround - those attributes aren't currently 
 *               available for mapping in EventStream unless we copy them.
 */


// copy all the dom.* attributes (dom.url is copied to dom_url_copy)
for (var i = 0, keys = Object.keys(b), key; i &lt; keys.length; i++) {
    key = keys[i];
    if (/^dom\..*/.test(key)) {
        b[key.replace(&quot;.&quot;, &quot;_&quot;) + &quot;_copy&quot;] = b[key];
    }
}

// add a millisecond-based timestamp
b.tealium_timestamp_epoch_ms = Date.now();

// taken from the Collect tag and modified to add them to b directly instead of the 'browser' sub-object
try{
    b[&quot;viewport_height&quot;] = window.innerHeight || document.body.clientHeight;
    b[&quot;viewport_width&quot;] = window.innerWidth || document.body.clientWidth;
    b[&quot;viewport_size&quot;] = b[&quot;viewport_width&quot;] + &quot;x&quot; + b[&quot;viewport_height&quot;];

    b[&quot;screen_height&quot;] = window.screen.height;
    b[&quot;screen_width&quot;] = window.screen.width;
    b[&quot;screen_size&quot;] = b[&quot;screen_width&quot;] + &quot;x&quot; + b[&quot;screen_height&quot;];

}catch(e){utag.DB(e);}

// doesn't work on old IEs, so it's in a separate TRY block
try{
    var color_depth = window.screen.colorDepth;
    if (color_depth) {
        b[&quot;color_depth&quot;] = color_depth + &quot;-bit&quot;;
    }

}catch(e){utag.DB(e);}

try{
    var user_language_setting =  window. navigator &amp;&amp; window.navigator.userLanguage || window.navigator &amp;&amp; window.navigator.language || &quot;&quot;;
    if (typeof user_language_setting === &quot;string&quot; &amp;&amp; user_language_setting !== &quot;&quot;) {
        b[&quot;user_language_setting&quot;] = user_language_setting.toLowerCase();
    }
}catch(e){utag.DB(e);}


try{
    var character_encoding =  document.characterSet || document.charset || &quot;&quot;;
    if (typeof character_encoding === &quot;string&quot; &amp;&amp; character_encoding !== &quot;&quot;) {
        b[&quot;character_encoding&quot;] = character_encoding;
    }
}catch(e){utag.DB(e);}

