/**
 * Scope       : Tealium Collect
 * Execution   : n/a
 * Condition   : n/a
 * Description : The EventStream GA Connector only supports same-length arrays for product custom dimension
 *               (actually everything product related), so we need to build them.
 * 
 */

var productArrayLength = b.product_ids.length || 0;

if (productArrayLength &gt; 0) {
    b.transaction_option_gs_product_array = repeatElementInArray(b.transaction_option_gs);
    b.transaction_option_product_array = repeatElementInArray(b.transaction_option);
    b.lottery_type_product_array = repeatElementInArray(b.lottery_type);
    b.transaction_currency_product_array = repeatElementInArray(b.transaction_currency);
}

function repeatElementInArray(el){
    var length = productArrayLength || 0
    var arr = [];
    for (var i = 0; i &lt; length; i++) {
        arr.push(el)
    }
    return arr;
}

