    var bt = bots_ua[i];
    if (RegExp(bt).test(agentString)) {rs = bt; break;} 
  }

  if (rs) {
    rs = rs.split(&quot;|&quot;)[0].toUpperCase()+&quot;_UA&quot;;
  } else {
    //Crawler, Spider, Bot im Agent String suchen
    if (/crawler/.test(agentString.toLowerCase())) rs = &quot;POTENTIAL_CRAWLER_UA&quot;; 
