  var name = cname + &quot;=&quot;;
  var ca = document.cookie.split(';');
  for(var i = 0; i &lt; ca.length; i++) {
    var c = ca[i];
    while (c.charAt(0) == ' ') {
      c = c.substring(1);
    }
    if (c.indexOf(name) === 0) {
      return c.substring(name.length, c.length);
    }
  }
  return &quot;&quot;;
}



