//Folgenden Code bei Verwendung des Google Tag Managers als benutzerdefinierte JS-Variable anlegen und 
//als Feld customTask bei Hits in den festzulegenden Feldern nutzen 
function searchDetection() {
  return function(model) {
    var r = document.referrer; //Referrer als Integrierte Variable aktivieren und hier auslesen
    //Suchmuster f√ºr Suchmaschinen mit &quot;versteckten&quot; Suchparametern
	var srch = /(panda-search\.org)|(suche\.t-online\.de)|(qwant\.com)|(metager\.de)|(duckduckgo\.com)|(ecosia\.org)|(android\.googlequicksearchbox\.)|(search\.yahoo\.com)|(zapmeta\.)|(\.sosodesktop\.com)|(search\.mysearch\.com)|(search\.mail\.com)|(thesmartsearch\.net)|(zdsearch\.com)|(search\.easydialsearch\.com)|(safesearch\.hypersonica\.com)|(search\.v9\.com)|(search\.genieo\.com)|(search\.avast\.com)|(searches\.uninstallmaster\.com)|(search\.handycafe\.com)|(searches\.safehomepage\.com)|(search\.snapdo\.com)|(search\.selfbutler\.com)|(infospace\.com)|(search\.avira\.)/i;
  //Referrer mit Suchmuster vergleichen
	if (r.search(srch) &gt; 0) {
      //passenden Parameter bestimmen
      var prm = (r.search('yahoo') &gt; 0) ? 'p=':'q=';
      //Konstanten Suchparameter anf√ºgen, wenn kein Parameter vorhanden ist
      if (r.search(prm) &lt; 0) 
        r += (r.split('?')[1] ? '&amp;':'?') + prm + '(not provided)';
      model.set('referrer', r);
      //Keine Kampagnenparameter (yahoo paid) gefunden? Dann setzen...
      if (r.indexOf('utm_source=') &lt; 0) {
        model.set('utm_source', r.replace('http://','').replace('https://','').replace('www.','').split(/[/?#]/)[0]);
        model.set('utm_medium', 'organic');
        var rgx = new RegExp('[\\?&amp;]' + prm + '([^&amp;#]*)');
        var q = rgx.exec(r);
        q = decodeURIComponent(q[1].replace(/\+/g, ' '));
        model.set('utm_term', q);
      }
    }
  };
}

