// look for new placeholder divs
function addStarsIfNeeded() {
  if (typeof $BV !== &quot;undefined&quot;) {
    //console.log(&quot;Checking if stars are needed&quot;);
    var containerPrefix = &quot;BVRRInlineRating&quot;;
    var dataAttributeName = &quot;data-itemid&quot;;

    var productIds = scrapeProductIdsForPlaceholders(containerPrefix, dataAttributeName);
    if (productIds.length &gt; 0) {
      $BV.ui( 'rr', 'inline_ratings', {
        &quot;productIds&quot; : productIds,
        &quot;containerPrefix&quot; : containerPrefix
      });
    }
  }
}

// launch the endless polling (we never know if the user can scroll for more products or not)
setInterval(addStarsIfNeeded, 800);

function scrapeProductIdsForPlaceholders(containerPrefix, dataAttributeName) {
  var productIds = [];
  // add the product ID to the placeholder node, and to the list
  function addProductId (node, productId) {
    node.setAttribute(dataAttributeName, String(productId));
    node.setAttribute(&quot;id&quot;, containerPrefix + &quot;-&quot; + productId);
    // node.style.background = &quot;green&quot;;
    productIds.push(productId);
    // console.log(productIds);
  }

  // find the placeholder divs (only finds currently loaded ones), exclude already scraped ones
  var placeholders = document.querySelectorAll(&quot;div.norating-placeholder:not([&quot; + dataAttributeName + &quot;])&quot;);
  for (var i = 0; i &lt; placeholders.length; i++) {
    var el = placeholders[i];
    var activeEl = el;

    while (activeEl.parentNode) {
      try {
        activeEl = activeEl.parentNode;
        // check for the first type of template (Bestsellers on the homepage)
        if (activeEl.hasAttribute &amp;&amp; activeEl.hasAttribute(dataAttributeName)) {
          addProductId(el, activeEl.getAttribute(dataAttributeName));
          break;
        }

        // check for the second type of template (Testsieger on the website)
        if (typeof activeEl.href === &quot;string&quot;) {
          var re = /.*\/(.+)\.html/g;
          var match = re.exec(activeEl.href);
          if (match !== null) {
            addProductId(el, match[1]);
            // console.log(match[1] + &quot; addded to el&quot;)
            break;
          }
        }
      }
      catch (e) {
        if (typeof console !== &quot;undefined&quot; &amp;&amp; typeof console.log !== &quot;undefined&quot;) {
          console.log(e)
        }
      }
    }
  }
  return productIds;
}

