  // enter cookie value set for new users who haven't interacted with the Cookie Manager at all
  config.default_cookie_value = &quot;_pcbc%3A0&quot;;

  // the opt_in level set by default for new users with the default_cookie_value set
  // supports: &quot;opt-out&quot;, &quot;required&quot;, &quot;comfort&quot;, &quot;statistics&quot;, &quot;targeting&quot;
  config.default_opt_in_level = &quot;required&quot;;

  // enter the cookie value set for users who have COMPLETELY opted out of cookies
