// Scope       : Pre Loader
// Execution   : n/a
// Condition   : n/a
// Description : PermissionsCheck 2: Read the previously set cookie configuration and set universal permissions
//               to be used in TiQ Load Rules


(function () {

  // allows global configuration / muting of console logging to aid debugging
  function logger(message) {
      console.info(&quot;**TiQ PermissionsCheck** - &quot; + message);
  }

  // stolen from utag.loader.lh, same logic used in TiQ to get the domain (not defined yet in Pre Loader)
  function getDomain(a, b, c) {
    a = &quot;&quot; + location.hostname;
    b = a.split(&quot;.&quot;);
    c = (/\.co\.|\.com\.|\.org\.|\.edu\.|\.net\.|\.asn\./.test(a)) ? 3 : 2;
    return b.splice(b.length - c, c).join(&quot;.&quot;);
  }

  // utility function
  function writeCookie (name, value, days) {
    var expires = &quot;&quot;;
    if (days) {
      var date = new Date();
      date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
      expires = &quot;; expires=&quot;+date.toGMTString();
    }
    document.cookie = name + &quot;=&quot; + encodeURIComponent(value) + expires + &quot;;domain=&quot; + getDomain() + &quot;;path=/&quot;;
    return true;
  }

  // utility function
  function readCookie (name) {
    var reString = '(?:(?:^|.*;\\s*)' + name + '\\s*\\=\\s*([^;]*).*$)|^.*$';
    var re = new RegExp(reString);
    var cookieValue = document.cookie.replace(re, &quot;$1&quot;);
    if (!cookieValue) return undefined;
    return decodeURIComponent(cookieValue);
  }

  // utility function
  function eraseCookie (name) {
    document.cookie = name + '=deleted;expires=Thu, 01 Jan 1970 00:00:01 GMT;domain=' + getDomain() + ';path=/;';
  }

  // stops EVERYTHING from working in TiQ - erases utag_main cookie and stops all tags from loading
  function blockAndDeleteTealiumCookie () {
    window.utag_cfg_ovrd = window.utag_cfg_ovrd || {};
    window.utag_cfg_ovrd.nocookie=true;
    eraseCookie(&quot;utag_main&quot;);
  };

  // stops EVERYTHING from working in TiQ - erases utag_main cookie and stops all tags from loading
  function blockTealiumLoad () {
    window.utag_cfg_ovrd = window.utag_cfg_ovrd || {};
    window.utag_cfg_ovrd.noload=true;
  };


  function getConfigValue (config, prop) {
    // if config or requested property not found, return undefined
    if (typeof config == &quot;undefined&quot; || config.hasOwnProperty(prop) == false) return undefined;
    // otherwise, return the user input from the config extension
    return config[prop];
  }

  function hasConfigValue (config, prop) {
    return typeof getConfigValue(config, prop) != &quot;undefined&quot;;
  }

  // checks if the user cookie value is an exact match for a target config settings, returns boolean
  function isMatch(config, userCookieValue, targetConfigSetting) {
    // user cookie is blank, return false
    if (userCookieValue == &quot;&quot;) return false;
    // config is missing or empty, return false
    if (typeof config[targetConfigSetting] == &quot;undefined&quot; || config[targetConfigSetting] == &quot;&quot;) return false;
    // STRICTLY compare the values, return the appropriate boolean
    return decodeURIComponent(userCookieValue) === decodeURIComponent(config[targetConfigSetting]);
  }

  function generatePermissions (userCookieValue, config, udo) {
      // the names of the config settings, in order of ASCENDING permission to track
      var permissionsHierarchy = [&quot;required&quot;, &quot;comfort&quot;, &quot;statistics&quot;, &quot;targeting&quot;];
      var match = &quot;false&quot;;
      // check each permission config for a match with the user cookie in DESCENDING order, stop when a match is found
      for (var i = permissionsHierarchy.length - 1, configVal, permissionName; i &gt;= 0; i--) {
        configPermissionName = &quot;cookie_value_for_&quot; + permissionsHierarchy[i] + &quot;_opt_in&quot;;
        udoPermissionName = &quot;allow_&quot; + permissionsHierarchy[i] + &quot;_cookies&quot;;
        if (isMatch(config, userCookieValue, configPermissionName)) {
          match = &quot;true&quot;;
          udo.user_permissions_level = permissionsHierarchy[i];
        }
        // change the permission for the matching value AND any lower-level permissions
        if (match == &quot;true&quot;) {
          udo[udoPermissionName] = &quot;true&quot;;
        }
      }
      // indicate whether a match was found
      udo.user_permissions_cookie_value_matches_config = match;
      return udo;
  }

  //********
  // Transform the profile-specific config settings into UNIVERSAL Bosch Permissions
  //********

  // get the config object
  var config = window.tealium_cookie_check.cookieConfig;

  var isMisconfigured = typeof config == &quot;undefined&quot; ||
                        typeof config.cookie_name == &quot;undefined&quot; || config.cookie_name == &quot;&quot;;
  var userCookieValue = readCookie(config.cookie_name);
  var userHasCookieValue = typeof userCookieValue != &quot;undefined&quot; &amp;&amp; userCookieValue != &quot;&quot;;

  // don't do ANYTHING if:
  // - the config object is missing or
  // - config.cookie_name is empty or missing or
  // - the named cookie isn't found or
  // - the named cookie is found but has an empty value
  if (isMisconfigured || !userHasCookieValue) {
    blockAndDeleteTealiumCookie();
    blockTealiumLoad();
    if (isMisconfigured) {
      logger(&quot;Missing config (or config.cookie_name) - kill TiQ load and remove 'utag_main' cookie&quot;);
    }
    if (!userHasCookieValue) logger(&quot;Missing cookie (or empty cookie value) - kill TiQ and remove 'utag_main' cookie&quot;);
    return;
  }

  // if the cookie value is the DEFAULT, set the userCookieValue to the one for the DEFAULT permission level
  var hasDefaultCookieValue = (isMatch(config, userCookieValue, &quot;default_cookie_value&quot;));
  if (hasDefaultCookieValue) {
    var defaultLevel = getConfigValue(config, &quot;default_opt_in_level&quot;);
    userCookieValue = getConfigValue(config, &quot;cookie_value_for_&quot; + defaultLevel + &quot;_opt_in&quot;);
  }

  // check if the user has opted out (also applies for users with default cookies and an 'opt-out' default setting)
  var userHasOptedOut = isMatch(config, userCookieValue, &quot;cookie_value_for_full_opt_out&quot;);

  // don't do ANYTHING if
  // - the cookie value is missing (or empty) OR
  // - the user has explicitly OR implicitly opted-out
  if (userHasOptedOut) {
      blockAndDeleteTealiumCookie();
      blockTealiumLoad();
      logger(&quot;User has opted out (or was opted out by default) - kill TiQ load and remove 'utag_main' cookie&quot;);
      return;
  }

  // initilize, err on the side of caution
  var udo = window.utag_data = window.utag_data || {};
  // use strings instead of booleans to make friendlier for TiQ Load Rules
  udo.allow_required_cookies = &quot;false&quot;;
  udo.allow_comfort_cookies = &quot;false&quot;;
  udo.allow_statistics_cookies = &quot;false&quot;;
  udo.allow_targeting_cookies = &quot;false&quot;;


  // set the permissions based on the user input and cookie config
  udo = generatePermissions(userCookieValue, config, udo);

  // check if the cookie value matched any of the configured ones, warn if not matched
  if (udo.user_permissions_cookie_value_matches_config == &quot;false&quot;) {
    blockAndDeleteTealiumCookie();
    blockTealiumLoad();
    logger(&quot;Cookie value '&quot; + userCookieValue + &quot;' doesn't match any configured options for cookie '&quot; + config.cookie_name + &quot;' - kill TiQ load and remove 'utag_main' cookie&quot;);
    return;
  }

  else logger(&quot;Set user permissions to '&quot; + udo.user_permissions_level + &quot;'&quot;);

}());

