 .privacy_prompt table td { 
     vertical-align: top; 
     padding: 10px 8px 5px 8px; 
 } 

 .privacy_prompt table tr td:first-child { 
     font-weight: 400; 
     color: #666; 
 } 

 .privacy_prompt table tr td:last-child { 
     text-align: center; 
