(function preferences_prompt(doc) {
  var $el = doc.getElementById(&quot;preferences_prompt_submit&quot;),
      $body = doc.getElementsByClassName(&quot;consent_preferences&quot;)[0],
      $closeBtn = $body.getElementsByClassName(&quot;close_btn_thick&quot;)[0],
      reg_match = /\d+$/;

  var callBack = function () {

    var inputs = $body.getElementsByClassName(&quot;toggle&quot;),
    cats = {};

    for (var i = 0; i &lt; inputs.length; i++) {
      var obj = inputs[i];
      cats[obj.id.match(reg_match)[0]] = obj.checked ? 1 : 0;
    }
    closePrompt();

    utag.gdpr.setPreferencesValues(cats);

    /* fire explicit consent function if defined*/
    if (utag.gdpr.onExplicitConsent) utag.gdpr.onExplicitConsent();

  };
  var closePrompt = function () {
    var $privacy_prompt = document.getElementById(&quot;__tealiumGDPRcpPrefs&quot;);
    $privacy_prompt.style.display = &quot;none&quot;;
  };
  if (doc.addEventListener) {
    $el.addEventListener(&quot;click&quot;, callBack, false);
    if($closeBtn){$closeBtn.addEventListener(&quot;click&quot;, closePrompt, false);}
  } else if (doc.attachEvent) {
    $el.attachEvent(&quot;click&quot;, callBack);
    if($closeBtn){$closeBtn.attachEvent(&quot;click&quot;, closePrompt);}
  } else {
    $el.onclick = callBack;
    if($closeBtn){$closeBtn.onclick = closePrompt;}
  }

  /*Process CONSENTMGR cookie*/
  var cookieValues = utag.gdpr.getCookieValues();
  inputs = $body.getElementsByClassName(&quot;toggle&quot;);
  if(cookieValues.consent == &quot;true&quot; &amp;&amp; typeof cookieValues.c1 == &quot;undefined&quot;){
      /*This means that the prompt manager was fully accepted and all categories should be enabled*/
      for (var i = 0; i &lt; inputs.length; i++) {
          inputs[i].checked = true;
      }
  }else if(cookieValues.consent == &quot;true&quot; &amp;&amp; typeof cookieValues.c1 != &quot;undefined&quot;){
      /*This means that we either have full consent or partial consent*/
      var cats = {};
      for (var i = 0; i &lt; inputs.length; i++) {
          /*Only check the category if this is enabled in the cookie*/
          var inputIDCategory = inputs[i].id.match(/(\d+)/)[1];
          cats[inputIDCategory] = parseInt(cookieValues[&quot;c&quot;+inputIDCategory]);
          if(cookieValues[&quot;c&quot;+inputIDCategory] === &quot;1&quot;){
              /*Category is enabled in the cookie, so update the UI to reflect the change*/
              inputs[i].checked = true;
          }
      }
      /*Provide fix for cookie consent being set to true regardless of actual consent given. Resetting preferences based on cookie category definition*/
      utag.gdpr.setPreferencesValues(cats);
  }
}(document));

