(function consent_prompt(){
  var $body = document.getElementsByClassName(&quot;explicit_consent&quot;)[0],
      $el = document.getElementById(&quot;consent_prompt_submit&quot;),
      $closeBtn = $body.getElementsByClassName(&quot;close_btn_thick&quot;)[0],
      $advancedBtn = document.getElementById(&quot;advanced_consent_options&quot;);

  var callBack = function () {
    var $privacy_pref_optin = document.getElementById(&quot;privacy_pref_optin&quot;),
        $privacy_pref_optout = document.getElementById(&quot;privacy_pref_optout&quot;);

    if ($privacy_pref_optin.checked) {
      utag.gdpr.setConsentValue(1);
      /* fire explicit consent function if defined*/
      if (utag.gdpr.onExplicitConsent) utag.gdpr.onExplicitConsent();
      /* Since the user has accepted all cookies, fire the page tracking call */
      utag.view(window[utag.udoname]);
    } else if ($privacy_pref_optout.checked) {
      utag.gdpr.setConsentValue(0);
      /* fire explicit consent function if defined*/
      if (utag.gdpr.onExplicitConsent) utag.gdpr.onExplicitConsent();
    } else {
      return;
    }
    closePrompt();
  };

  var closePrompt = function(){
    var $privacy_prompt = document.getElementById(&quot;__tealiumGDPRecModal&quot;);
    $privacy_prompt.style.display = &quot;none&quot;;
  };
  if (document.addEventListener) {
    $el.addEventListener(&quot;click&quot;, callBack, false);
    if($closeBtn){$closeBtn.addEventListener(&quot;click&quot;, closePrompt, false);}
    if($advancedBtn){$advancedBtn.addEventListener(&quot;click&quot;, advancedOptions, false);}
  } else if (document.attachEvent) {
    $el.attachEvent(&quot;click&quot;, callBack);
    if($closeBtn){$closeBtn.attachEvent(&quot;click&quot;, closePrompt);}
    if($advancedBtn){$advancedBtn.attachEvent(&quot;click&quot;, advancedOptions);}
  } else {
    $el.onclick = callBack;
    if($closeBtn){$closeBtn.onclick = closePrompt;}
    if($advancedBtn){$advancedBtn.onclick = advancedOptions;}
  }
  function advancedOptions(){
      closePrompt();
      utag.gdpr.showConsentPreferences();
  }
  /*Process CONSENTMGR cookie*/
  var cookieValues = utag.gdpr.getCookieValues();
  if(typeof cookieValues.consent !== 'undefined'){
      /*This means that the user has made a preference choice before.  Let's show that value*/
      if(cookieValues.consent == 'true'){
        document.getElementById('privacy_pref_optin').click();
      }else{
        document.getElementById('privacy_pref_optout').click();
      }
  }
}());

