/**
 * Scope       : DOM Ready
 * Execution   : n/a
 * Condition   : n/a
 * Description : GTM Listener - Add listener function using the DataLayer Helper
 * Notes       : Both new and legacy objects need to be converted for the current migration. NEW: window.dataLayer, Legacy: window.tui.analytics
 */

var flatNew = {},
    flatLegacy = {},
    flatMerged = {};

// flattening legacy object before the model process, to be merged with new DL
// using prefix as test for now to differentiate and avoid overrides when merging data for same keys with differing values
if (window.tui.analytics) {
    teal.prefix = 'legacy_';
    flatLegacy = teal.flattenObject(window.tui.analytics);
}

// optional tbd
var triggerLegacyOrBackup = function() {
    return Object.keys(flatLegacy).length &gt; 0 ? utag.view(flatLegacy) : console.log('***** Tealium view did not trigger, no page data available!', utag.data);
};

// start processing the model event
var processEvent = function(model, message) {
    // exit if the flattener function isn't present on the page
    if (!teal || typeof teal.flattenObject !== 'function') {
        return;
    }

    teal.prefix = 'new_';
    // utag.view on gtm.load to help see dataLayer for now
    if (message.event === 'gtm.load') {
        flatNew = teal.flattenObject(model, model);
        flatMerged = Object.assign(flatNew, flatLegacy);

        console.log('***** initializing VIEW from GTM listener!');
        utag.view(flatMerged);
        utag.data = flatMerged;

    } else {
        // decide if view should trigger when no event data
        // triggerLegacyOrBackup();
    }

    // some click events as examples
    if (message.event === 'buttonClick' || message.event === 'GAevent') {
        flatNew = teal.flattenObject(model, model);
        flatMerged = Object.assign(flatNew, flatLegacy);

        console.log('***** initializing LINK from GTM listener!');
        utag.link(flatMerged);
        utag.data = flatMerged;
    }
};


window.dataLayer = window.dataLayer|| [];
// using helper for new data only
if (typeof processEvent === 'function') {
    var helper = new DataLayerHelper(window.dataLayer, processEvent, true);
}

