// Used for Timestamp eVar
function checkTime(i) {
    if (i &lt; 10) {
        i = &quot;0&quot; + i;
    }
return i;
}

function startTime() {
    var today = new Date();
    var h = today.getHours();
    var m = today.getMinutes();
    var s = today.getSeconds();
    // add a zero in front of numbers&lt;10
    m = checkTime(m);
    s = checkTime(s);
    h = checkTime(h);
	return h + &quot;:&quot; + m + &quot;:&quot; + s;
}

