/* Plugin: Performance Timing Tracking - 0.1 BETA */
s.performanceTiming = function(v){
    var s=this;
    if(v) s.ptv=v;
    if(typeof performance!='undefined'){
        if(performance.timing.loadEventEnd==0){
            s.pi=setInterval(function(){s.performanceWrite()},250);
        }
        if(!s.ptc||s.linkType=='e'){
            s.performanceRead();
        }
        else{s.rfe();
        s[s.ptv]='';
        }
     }
};

s.performanceWrite = function(){
    var s=this;
    if(performance.timing.loadEventEnd&gt;0)clearInterval(s.pi);
    try{
        if(s.c_r('s_ptc')==''&amp;&amp;performance.timing.loadEventEnd&gt;0){
            try{
                var pt=performance.timing;
                var pta='';
                pta=s.performanceCheck(pt.fetchStart,pt.navigationStart);
                pta+='^^'+s.performanceCheck(pt.domainLookupStart,pt.fetchStart);
                pta+='^^'+s.performanceCheck(pt.domainLookupEnd,pt.domainLookupStart);
                pta+='^^'+s.performanceCheck(pt.connectEnd,pt.connectStart);
                pta+='^^'+s.performanceCheck(pt.responseStart,pt.connectEnd); 
                pta+='^^'+s.performanceCheck(pt.responseEnd,pt.responseStart);
                pta+='^^'+s.performanceCheck(pt.loadEventStart,pt.domLoading);
                pta+='^^'+s.performanceCheck(pt.loadEventEnd,pt.loadEventStart);
                pta+='^^'+s.performanceCheck(pt.loadEventEnd,pt.navigationStart);
				pta+='^^'+s.performanceCheck(performance.getEntriesByType('paint')[0].startTime,0); // Added for First Paint Metric
				pta+='^^'+s.performanceCheck(performance.getEntriesByType('paint')[1].startTime,0); // Added for First Contentful Paint Metric
                s.c_w('s_ptc',pta);
                    if(sessionStorage&amp;&amp;navigator.cookieEnabled&amp;&amp;s.ptv!='undefined'){
                        var pe=performance.getEntries();
                        var tempPe='';
                        for(var i=0;i&lt;pe.length;i++){
                            tempPe+='!';tempPe+=pe[i].name.indexOf('?')&gt;-1?pe[i].name.split('?')[0]:pe[i].name;
                            tempPe+='|'+(Math.round(pe[i].startTime)/1000).toFixed(1)+'|'+(Math.round(pe[i].duration)/1000).toFixed(1)+'|'+pe[i].initiatorType;}
                            sessionStorage.setItem('s_pec',tempPe);
                    }
            }
            catch(err){
                return;
            }
        }
    }
    catch(err){
        return;
    }
};
s.performanceCheck = function(a,b){
    if(a&gt;=0&amp;&amp;b&gt;=0){
        if((a-b)&lt;60000&amp;&amp;((a-b)&gt;=0)){
            return((a-b)/1000).toFixed(2);
        }
        else{
            return 600;
        }
    }
};
s.performanceRead = function(){
    var s=this;
    if(performance.timing.loadEventEnd&gt;0)clearInterval(s.pi);
    var cv=s.c_r('s_ptc');
    if(s.pte){
        var ela=s.pte.split(',');
    }
    if(cv!=''){
        var cva=s.split(cv,'^^');
        if(cva[1]!=''){
            for(var x=0;x&lt;(ela.length-1);x++){
                s.events=s.apl(s.events,ela[x]+'='+cva[x],',',2);
            }
        }
        s.events=s.apl(s.events,ela[ela.length-1],',',2);
    }
    s.linkTrackEvents=s.apl(s.linkTrackEvents,s.pte,',',2);
    s.c_w('s_ptc','',0);
    if(sessionStorage&amp;&amp;navigator.cookieEnabled&amp;&amp;s.ptv!='undefined'){
        s[s.ptv]=sessionStorage.getItem('s_pec');
        sessionStorage.setItem('s_pec','',0);
    }
    else{
        s[s.ptv]='sessionStorage Unavailable';
    }
    s.ptc=true;
}

s.rfe = function(){
    var s=this;
    var ea=s.split(s.events,',');
    var pta=s.split(s.pte,',');
    try{
        for(x in pta){
            s.events=s.rfl(s.events,pta[x]);
            s.contextData['events']=s.events;
        }
    }
    catch(e){
        return;
    }
}

s.rfl = function(l,v,d1,d2,ku){
	var s=this,
		R=new Array(),
		C='',
		d1=!d1?',':d1,
		d2=!d2?',':d2,
		ku=!ku?0:1;
	if(!l)return'';
	L=l.split(d1);
	for(i=0;i&lt;L.length;i++){
		if(L[i].indexOf(':')&gt;-1){ // remove serialisation to get just the event number
			C=L[i].split(':');
			C[1]=C[0]+':'+C[1];
			L[i]=C[0];
		}
		if(L[i].indexOf('=')&gt;-1){ // remove numeric event to get just the event number
			C=L[i].split('=');
			C[1]=C[0]+'='+C[1];
			L[i]=C[0];
		}
		if(L[i]!=v&amp;&amp;C){ // if it wasn't a duplicate and the variable had extras
			R.push(C[1])
		}
		else if(L[i]!=v){ // if it was a duplicate
			R.push(L[i])
		}
		else if(L[i]==v&amp;&amp;ku){
			ku=0;
			if(C){
				R.push(C[1])
			}
			else {
				R.push(L[i])
			}
		}
		C=''; // clear it back out
	}
	return s.join(R,{delim:d2})
}


