                            tempPe+='|'+(Math.round(pe[i].startTime)/1000).toFixed(1)+'|'+(Math.round(pe[i].duration)/1000).toFixed(1)+'|'+pe[i].initiatorType;}
                            sessionStorage.setItem('s_pec',tempPe);
                    }
            }
            catch(err){
                return;
            }
        }
    }
    catch(err){
        return;
    }
};
s.performanceCheck = function(a,b){
    if(a&gt;=0&amp;&amp;b&gt;=0){
        if((a-b)&lt;60000&amp;&amp;((a-b)&gt;=0)){
            return((a-b)/1000).toFixed(2);
        }
        else{
            return 600;
        }
    }
};
s.performanceRead = function(){
    var s=this;
    if(performance.timing.loadEventEnd&gt;0)clearInterval(s.pi);
    var cv=s.c_r('s_ptc');
    if(s.pte){
        var ela=s.pte.split(',');
    }
