      return []
    }

    cmpConsentArray &amp;&amp; cmpConsentArray.forEach(function (tagConsent) {
      if (tagConsent.consentStatus === true) {
        vendorArray.push(tagConsent.dataProcessingService)
      }
    })
    vendorArray.type = checkForExplicitConsentDecision(cmpConsentArray) ? 'explicit' : 'implicit'

    if (checkForTiqConsent(cmpConsentArray) === false) {
      // change the consent type, but leave the array for debugging purposes
      vendorArray.type = 'missing-tiq-consent'
    }

